      initial={{ opacity: 0, y: 30 }}     // ‚úÖ animation start
      animate={{ opacity: 1, y: 0 }}      // ‚úÖ animation end
      transition={{ duration: 0.6 }}      // ‚úÖ smooth
    >
      <h2>{weather.name}</h2>
      <p>üå° Temperature: {weather.main.temp}¬∞C</p>
      <p>‚òÅ Condition: {weather.weather[0].description}</p>
      <p>üí® Wind Speed: {weather.wind.speed} m/s</p>
    </motion.div>
  );
};

export default Weather;
